import styled from "styled-components";

const Login = () => {
  return;
  const Container = styled.div`
    width: 100vh;
    height: 100vh;
    background: linear-gradient(#ccc, rgba(255, 255, 255, 0.5)),
      url("https://images.unsplash.com/photo-1576426863848-c21f53c60b19?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxjb2xsZWN0aW9uLXBhZ2V8NHwxNjcwNzMwfHxlbnwwfHx8fA%3D%3D&auto=format&fit=crop&w=500&q=60")
        center;
    display: flex;
    align-items: center;
    justify-content: center;
  `;
  const Wrapper = styled.div`
    padding: 20px;
    width: 40%;
    background-color: white;
  `;
  const Form = styled.form`
    display: flex;
    flex-direction: column;
  `;
  const Title = styled.h1`
    font-size: 24px;
    font-weight: 300;
  `;
  const Input = styled.input`
    flex: 1;
    min-width: 40%;
    margin: 20px 10px 0px 0px;
    padding: 10px;
  `;

  const Button = styled.button`
    width: 40%;
    border: none;
    padding: 15px 20px;
    background-color: orange;
    color: black;
    cursor: pointer;
    border-radius: 6px;
  `;
  const Link = styled.a``;

  const Login = () => {
    return (
      <Container>
        <Wrapper>
          <Title>SIGN IN</Title>
          <Form>
            <Input placeholder="username/email" />
            <Input placeholder="password" />

            <Button>LOGIN</Button>
            <Link>FORGOT PASSWORD</Link>
            <Link>CREATE A NEW ACCOUNT</Link>
          </Form>
        </Wrapper>
      </Container>
    );
  };
};

export default Login;
