import styled from "styled-components";
import Footer from "../Footer";
import Headline from "../Headline";
import Navbar from "../Navbar";
import Newsletter from "../Newsletter";

const Container = styled.div``;
const Wrapper = styled.div`
  padding: 50px;
  display: flex;
`;
const ImgContainer = styled.div`
  flex: 1;
`;
const Image = styled.img`
  width: 100%;
  //height: 90vh;
  object-fit: cover;
`;
const InfoContainer = styled.div`
  flex: 1;
  padding: 0px 50px;
`;
const Title = styled.h1`
  font-weight: 200;
`;
const Desc = styled.p`
  margin: 20px 0px;
`;
const Price = styled.span`
  font-weight: 100;
  font-size: 30px;
`;

const FilterContainer = styled.div``;
const Filter = styled.div``;
const FilterTitle = styled.div``;
const FilterColour = styled.div``;
const FilterSize = styled.select``;
const FilterSizeOption = styled.option``;

const Product = () => {
  return (
    <Container>
      <Navbar />
      <Headline />
      <Wrapper>
        <ImgContainer>
          <Image src="/images/1__4_-removebg-preview.png" />
        </ImgContainer>

        <InfoContainer>
          <Title>Red gown</Title>
          <Desc>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Numquam sed
            iste illum quasi velit ducimus temporibus totam et vitae porro
            molestiae magnam id accusamus ipsam ratione, quam reiciendis
            voluptatem nihil.
          </Desc>
          <Price>N5,000</Price>
          <FilterContainer>
            <Filter>
              <FilterTitle>Colour</FilterTitle>
              <FilterColour colour="black" />
              <FilterColour colour="darkblue" />
              <FilterColour colour="gray" />
            </Filter>
            <Filter>
              <FilterTitle>Size</FilterTitle>
              <FilterSize>
                <FilterSizeOption>XS</FilterSizeOption>
                <FilterSizeOption>S</FilterSizeOption>
                <FilterSizeOption>M</FilterSizeOption>
                <FilterSizeOption>L</FilterSizeOption>
                <FilterSizeOption>XL</FilterSizeOption>
                <FilterSizeOption>XXL</FilterSizeOption>
              </FilterSize>
            </Filter>
          </FilterContainer>
        </InfoContainer>
      </Wrapper>

      <Newsletter />
      <Footer />
    </Container>
  );
};

export default Product;
