import styled from "styled-components";
import Footer from "../Footer";
import Headline from "../Headline";
import Navbar from "../Navbar";
import Newsletter from "../Newsletter";

const Container = styled.div``;
const Wrapper = styled.div`
  padding: 50px;
  display: flex;
`;
const ImgContainer = styled.div`
  flex: 1;
`;
const Image = styled.img`
  width: 100%;
  //height: 90vh;
  object-fit: cover;
`;
const InfoContainer = styled.div`
  flex: 1;
  padding: 0px 50px;
`;
const Title = styled.h1``;
const Desc = styled.p``;
const Price = styled.span``;

const Product = () => {
  return (
    <Container>
      <Navbar />
      <Headline />
      <Wrapper>
        <ImgContainer>
          <Image src="/images/download-removebg-preview.png" />
        </ImgContainer>

        <InfoContainer>
          <Title>Hand bag</Title>
          <Desc>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Numquam sed
            iste illum quasi velit ducimus temporibus totam et vitae porro
            molestiae magnam id accusamus ipsam ratione, quam reiciendis
            voluptatem nihil.
          </Desc>
          <Price>N5,000</Price>
        </InfoContainer>
      </Wrapper>

      <Newsletter />
      <Footer />
    </Container>
  );
};

export default Product;
